[0, 31, "m'\nGREEN='033[0;32m'\nYELLOW='033[1;33m'\nBLUE='033[0;34m'\nCYAN='033[0;36m'\nNC='033[0m' # No Color\n\n# Functions\nlog_info() {\n    echo -e", {"\n}\n\nlog_success() {\n    echo -e": {"\n}\n\nlog_error() {\n    echo -e": {"\n}\n\nlog_warning() {\n    echo -e": {"\n}\n\n# Check environment variables\nlog_info \"Checking environment variables...\"\nif [ -z \"$NEXT_PUBLIC_SUPABASE_URL": "then\n    log_error", "Please set it in .env.local or run:\"\n    echo": "xport NEXT_PUBLIC_SUPABASE_URL=https://sfckfiwuobgmxelsvqnw.supabase.co", "$NEXT_PUBLIC_SUPABASE_ANON_KEY": "then\n    log_error", "Please set it in .env.local\"\n    exit 1\nfi\n\nlog_success \"Environment variables found\"\necho \"": "Extract project ID\nPROJECT_ID=$(echo $NEXT_PUBLIC_SUPABASE_URL | sed 's/https://([^.]*).*/1/')\nlog_info", "ID": "PROJECT_ID", "DASHBOARD_URL=": "ttps://supabase.com/dashboard/project/$PROJECT_ID", "Dashboard URL: $DASHBOARD_URL\"\nlog_info": "QL Editor URL: $SQL_EDITOR_URL", "Checking if database tables exist...\"\n\nTABLES_CHECK=$(curl -s": "ttps://$PROJECT_ID.supabase.co/rest/v1/customers?select=id&limit=1", "apikey: $NEXT_PUBLIC_SUPABASE_ANON_KEY\" \n  -H": "uthorization: Bearer $NEXT_PUBLIC_SUPABASE_ANON_KEY", "$TABLES_CHECK\" | grep -q \"code.*PGRST\"; then\n    log_warning \"Database tables do not exist yet\"\n    TABLES_EXIST=false\nelif echo \"$TABLES_CHECK\" | grep -q": ["; then\n    log_success \"Database tables exist (empty result set expected)\"\n    TABLES_EXIST=true\nelse\n    log_warning \"Unknown response (tables may exist)\"\n    TABLES_EXIST=true\nfi\n\necho \"", "if [ \"$TABLES_EXIST", true], "Database schema is already deployed!\"\n    echo \"": "og_info", "first": "\n    echo \"  $SQL_EDITOR_URL\"\n    echo \"", "==========================================================================\"\necho \"DEPLOYMENT INSTRUCTIONS\"\necho \"==========================================================================\"\necho \"": "og_warning", "you need to deploy the schema manually via the web dashboard.\"\necho \"": "og_info", "  $SQL_EDITOR_URL\"\necho \"": "og_info", "  File: $(pwd)/supabase/migrations/001_initial_schema.sql\"\necho": "ile: $(pwd)/supabase/migrations/002_additional_tables.sql", "Step 3: Run the migration files in order:\"\necho \"  1. Paste 001_initial_schema.sql\"\necho \"  2. Click 'Run'\"\necho \"  3. Wait for completion\"\necho \"  4. Paste 002_additional_tables.sql\"\necho \"  5. Click 'Run'\"\necho \"": "cho", "\necho": "LTERNATIVE: Supabase CLI (if you have access)", "==========================================================================\"\necho \"": "og_info", "commands": "\necho \"", "${BLUE}  supabase login${NC}\"\necho -e": {"${BLUE}  supabase db push${NC}\"\necho \"": "cho", "\necho \"": "og_info", "with": "\necho -e"}}}}}]