[[0, 31, "m'\nGREEN='033[0;32m'\nYELLOW='033[1;33m'\nBLUE='033[0;34m'\nCYAN='033[0;36m'\nNC='033[0m' # No Color\n\n# Load environment variables\nif [ -f .env.local ]; then\n    export $(grep -v '^#' .env.local | xargs)\nfi\n\n# Functions\nlog_info() {\n    echo -e \"${BLUE}\u2139${NC} $1\"\n}\n\nlog_success() {\n    echo -e \"${GREEN}\u2713${NC} $1\"\n}\n\nlog_error() {\n    echo -e \"${RED}\u2717${NC} $1\"\n}\n\nlog_warning() {\n    echo -e \"${YELLOW}\u26a0${NC} $1\"\n}\n\n# Check environment variables\nlog_info \"Checking environment variables...\"\nif [ -z \"$NEXT_PUBLIC_SUPABASE_URL"], ["z \"$NEXT_PUBLIC_SUPABASE_ANON_KEY"], ["."], ["; then\n    log_success \"Database tables exist (empty result set expected)\"\n    TABLES_EXIST=true\nelse\n    log_warning \"Unknown response (tables may exist)\"\n    TABLES_EXIST=true\nfi\n\necho \"", "Provide instructions based on status\nif [ \"$TABLES_EXIST", true], {"${BLUE}  supabase db push${NC}\"\necho \"": "cho"}]